                            int start = fullText.length() - 1;  // Start of "2"
                            int end = fullText.length();  // End of "2"
                            spannableString.setSpan(new RelativeSizeSpan(0.9f), start, end, Spanned.SPAN_EXCLUSIVE_EXCLUSIVE);  // 0.9f to make it smaller

