    private String truncateTextToWords(String text, int maxWords) {
        String[] words = text.split("\\s+");
        if (words.length <= maxWords) {
            return text;
        } else {
            return TextUtils.join(" ", Arrays.copyOfRange(words, 0, maxWords)) + " ...";
        }
    }



